class Solution:
    def findSubstringInWraproundString(self, p: str) -> int:
        dp = [0] * 26
        cnt = 1
        for i in range(len(p)):
            idx = ord(p[i])-97
            if i != 0 and (ord(p[i])-ord(p[i-1]) == 1 or ord(p[i])-ord(p[i-1]) == -25):
                cnt += 1
            else:
                cnt = 1
            dp[idx] = max(cnt, dp[idx])
        return sum(dp)







sol = Solution()
p = ""
ans = sol.findSubstringInWraproundString(p)
print(ans)